import React from "react";

class Casino extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			hand: [props.cards[Math.floor(Math.random() * 12)]],
		};
	}

	addCard = () => {
		let newCards = this.state.hand.push(
			this.props.cards[Math.floor(Math.random() * 12)],
		);
		this.setState({
			hand: newCards,
		});
	};

	render() {
		return (
			<div className="casino">
				{/* {this.state.hand.map(card => (
					<div className="card">{card}</div>
				))} */}
			</div>
		);
	}
}

export default Casino;
