import React from "react";

class Casino extends React.Component {
	state = {
		hand: [],
	};

	componentDidMount() {
		this.addCard();
		this.addCard();
	}

	addCard = () => {
		let newCards = this.state.cards.push(Math.floor(Math.random() * 12));
		this.setState({
			hand: newCards,
		});
	};

	render() {
		return (
			<div className="casino">
				{this.state.hand.map(card => (
					<div className="card">{card}</div>
				))}
			</div>
		);
	}
}

export default Casino;
