import React from "react";

class Player extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			hand: [
				props.cards[Math.floor(Math.random() * 12)],
				props.cards[Math.floor(Math.random() * 12)],
			],
			value: 0,
			hasAce: false,
			aceValue: 11,
		};
	}

	componentDidMount() {
		this.calculateValue();
		this.hasAce();
	}

	addCard = () => {
		let newCard = this.props.cards[Math.floor(Math.random() * 12)];
		let hand = this.state.hand;
		hand.push(newCard);

		this.setState({
			hand: hand,
		});

		this.hasAce();

		if (this.hasAce) {
			if (this.state.value > 21) {
				this.setState({ aceValue: 1 });
			} else if (this.state.value > 10 && newCard === "A") {
				this.setState({ aceValue: 1 });
			}
		}

		this.calculateValue();

		this.checkTurn();
	};

	hasAce = () =>
		this.state.hand.includes("A") ? this.setState({ hasAce: true }) : null;

	cardValue = card => {
		if (card === "J" || card === "Q" || card === "K") {
			return 10;
		} else if (card === "A") {
			return this.state.aceValue;
		} else {
			return card;
		}
	};

	calculateValue = () => {
		let handValue = this.state.hand.reduce((acc, curr) => {
			return acc + this.cardValue(curr);
		}, 0);

		this.setState({
			value: handValue,
		});
	};

	checkTurn = () => (this.state.value >= 21 ? this.finishTurn() : null);

	finishTurn = () => {
		alert("finished");
	};

	render() {
		return (
			<div className="player">
				{this.state.hand.map(card => (
					<div className="card">{card}</div>
				))}
				<div className="controller">
					<button>Stand</button>
					<button onClick={this.addCard}>Hit</button>
				</div>
			</div>
		);
	}
}

export default Player;
