import React from "react";

class Board extends React.Component {
	state = {
		playing: false,
	};

	handleStartGame = () => {
		this.setState({ playing: !this.state.playing });
	};

	render() {
		return (
			<div className="board">
				<button onClick={this.handleStartGame}>Play</button>
			</div>
		);
	}
}

export default Board;
