import React from "react";
import "./Components.css";
import Cell from "./Cell";

class Board extends React.Component {
	state = {
		cells: [],
	};

	componentDidMount() {
		let newCells = [];

		for (let i = 0; i < 10; i++) {
			for (let j = 0; j < 10; j++) {
				let cell = { row: i, col: j, value: Math.floor(Math.random() * 10) };
				newCells.push(cell);
			}
		}

		this.setState({ cells: newCells });
	}

	render() {
		return (
			<div className="board">
				{this.state.cells.map(cell => (
					<Cell {...cell} />
				))}
			</div>
		);
	}
}

export default Board;
