import React from "react";

class Player extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			hand: [
				props.cards[Math.floor(Math.random() * 12)],
				props.cards[Math.floor(Math.random() * 12)],
			],
			value: 0,
			hasAce: false,
		};
	}

	componentDidMount() {
		this.calculateValue();
		this.hasAce();
	}

	addCard = () => {
		let hand = this.state.hand;
		hand.push(this.props.cards[Math.floor(Math.random() * 12)]);

		this.setState({
			hand: hand,
		});

		this.calculateValue();
		this.hasAce();
	};

	hasAce = () =>
		this.state.hand.includes("A") ? this.setState({ hasAce: true }) : null;

	cardValue = card => {
		if (card === "J" || card === "Q" || card === "K") {
			return 10;
		} else if (card === "A") {
			return 11;
		} else {
			return card;
		}
	};

	calculateValue = () => {
		this.setState({
			value: this.state.hand.reduce((acc, curr) => {
				return acc + this.cardValue(curr);
			}, 0),
		});
	};

	render() {
		return (
			<div className="player">
				{this.state.hand.map(card => (
					<div className="card">{card}</div>
				))}
				<div className="controller">
					<button>Stand</button>
					<button onClick={this.addCard}>Hit</button>
				</div>
			</div>
		);
	}
}

export default Player;
