import React from "react";

class Casino extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			hand: [
				this.props.cards[Math.floor(Math.random() * 12)],
				this.props.cards[Math.floor(Math.random() * 12)],
			],
		};
	}

	componentDidMount() {
		this.addCard();
		this.addCard();
	}

	addCard = () => {
		let newCards = this.state.hand.push(Math.floor(Math.random() * 12));
		this.setState({
			hand: newCards,
		});
	};

	render() {
		return (
			<div className="casino">
				{this.state.hand.map(card => (
					<div className="card">{card}</div>
				))}
			</div>
		);
	}
}

export default Casino;
