import React from "react";
import "./Components.css";
import Cell from "./Cell";

class Board extends React.Component {
	state = {
		cells: [],
		score: 0,
		isAlive: true,
	};

	componentDidMount() {
		let newCells = [];

		for (let i = 0; i < 10; i++) {
			for (let j = 0; j < 10; j++) {
				let cell = {
					row: i,
					col: j,
					isBomb: Math.floor(Math.random() * 10) < 1,
				};
				newCells.push(cell);
			}
		}

		this.setState({ cells: newCells });
	}

	updateScore = () => {
		this.setState({ score: ++this.state.score });
	};

	endGame = () => {
		this.setState({ isAlive: false });
		alert(`BOOM!ðŸ’¥\nYour Score was ${this.state.score}`);
		this.props.finishGame();
	};

	calculateSurroundings = (row, col) => {
		let rowUp = row === 0 ? row : row - 1;
		let rowDown = row === 9 ? row : row + 1;
		let colLeft = col === 0 ? col : col - 1;
		let colRight = col === 9 ? col : col + 1;

		let cell1 = !!this.state.cells.find(
			cell => cell.row === rowUp && cell.col === col,
		).isBomb
			? 1
			: 0;

		let cell2 = !!this.state.cells.find(
			cell => cell.row === rowUp && cell.col === colRight,
		).isBomb
			? 1
			: 0;

		let cell3 = !!this.state.cells.find(
			cell => cell.row === row && cell.col === colRight,
		).isBomb
			? 1
			: 0;

		let cell4 = !!this.state.cells.find(
			cell => cell.row === rowDown && cell.col === colRight,
		).isBomb
			? 1
			: 0;

		let cell5 = !!this.state.cells.find(
			cell => cell.row === rowDown && cell.col === col,
		).isBomb
			? 1
			: 0;

		let cell6 = !!this.state.cells.find(
			cell => cell.row === rowDown && cell.col === colLeft,
		).isBomb
			? 1
			: 0;

		let cell7 = !!this.state.cells.find(
			cell => cell.row === row && cell.col === colLeft,
		).isBomb
			? 1
			: 0;

		let cell8 = !!this.state.cells.find(
			cell => cell.row === rowUp && cell.col === colLeft,
		).isBomb
			? 1
			: 0;

		return cell1 + cell2 + cell3 + cell4 + cell5 + cell6 + cell7 + cell8;
	};

	render() {
		return (
			<>
				<h3>{`Score: ${this.state.score}`}</h3>
				<div className="board">
					{this.state.cells.map(cell => (
						<Cell
							{...cell}
							updateScore={this.updateScore}
							endGame={this.endGame}
							calculateSurroundings={this.calculateSurroundings}
						/>
					))}
				</div>
			</>
		);
	}
}

export default Board;
